mapper.select:
SELECT BS_STOCKPILE_SPLIT_DETAIL_ID AS bsStockpileSplitDetailId , SELL_SIDE_NAME AS sellSideName , SELL_SIDE_CODE AS sellSideCode , ITEM_CODE AS itemCode , ITEM_NAME AS itemName , BRAND_CODE AS brandCode , BRAND_NAME AS brandName , ITEM_BRAND_CODE AS itemBrandCode , MANUFACTURER_CODE AS manufacturerCode , MANUFACTURER_NAME AS manufacturerName , SELLER_CODE AS sellerCode , SELLER_NAME AS sellerName , BATCH_CODE AS batchCode , SALE_LABEL_CODE AS saleLabelCode , SALE_LABEL_NAME AS saleLabelName , SMALL_PACKAGE_FEATURE AS smallPackageFeature , QUANTITY_PER_PACKAGE AS quantityPerPackage , NET_WEIGHT AS netWeight , GROSS_WEIGHT AS grossWeight , CARTON_SIZE AS cartonSize , PRODUCT_LEVEL AS productLevel , PALLET_STANDARD_STACK_QUANTITY AS palletStandardStackQuantity , GOODS_CODE AS goodsCode , GOODS_NAME AS goodsName , MAKER_CODE AS makerCode , MAKER_NAME AS makerName , DEL_FLG AS delFlg , CRT_ID AS crtId , CRT_TIME AS crtTime , UPD_ID AS updId , UPD_TIME AS updTime , VERSION AS version FROM ORDR_BS_STOCKPILE_SPLIT_DETAIL_ATTACHED
AND BS_STOCKPILE_SPLIT_DETAIL_ID = #{bsStockpileSplitDetailId}
AND DEL_FLG=#{delFlg}

SELECT BS_STOCKPILE_SPLIT_DETAIL_ID AS bsStockpileSplitDetailId , SELL_SIDE_NAME AS sellSideName , SELL_SIDE_CODE AS sellSideCode , ITEM_CODE AS itemCode , ITEM_NAME AS itemName , BRAND_CODE AS brandCode , BRAND_NAME AS brandName , ITEM_BRAND_CODE AS itemBrandCode , MANUFACTURER_CODE AS manufacturerCode , MANUFACTURER_NAME AS manufacturerName , SELLER_CODE AS sellerCode , SELLER_NAME AS sellerName , BATCH_CODE AS batchCode , SALE_LABEL_CODE AS saleLabelCode , SALE_LABEL_NAME AS saleLabelName , SMALL_PACKAGE_FEATURE AS smallPackageFeature , QUANTITY_PER_PACKAGE AS quantityPerPackage , NET_WEIGHT AS netWeight , GROSS_WEIGHT AS grossWeight , CARTON_SIZE AS cartonSize , PRODUCT_LEVEL AS productLevel , PALLET_STANDARD_STACK_QUANTITY AS palletStandardStackQuantity , GOODS_CODE AS goodsCode , GOODS_NAME AS goodsName , MAKER_CODE AS makerCode , MAKER_NAME AS makerName , DEL_FLG AS delFlg , CRT_ID AS crtId , CRT_TIME AS crtTime , UPD_ID AS updId , UPD_TIME AS updTime , VERSION AS version FROM ORDR_BS_STOCKPILE_SPLIT_DETAIL_ATTACHED
AND BS_STOCKPILE_SPLIT_DETAIL_ID IN (#{id})
AND DEL_FLG=#{delFlg}

